import { useState } from 'react';

function DisplaySingleQuiz({quiz}){
  const [displayQuestion, setdisplayQuestion] = useState(false)
  let questionsList = quiz.qa.map(questionItem => {return <li key={questionItem.id}>{questionItem.question}, {questionItem.answer}</li>})
  return (
    <>
      <li key={quiz.id} className='quiz' onCLick={()=>setdisplayQuestion(!displayQuestion)}> {quiz.title} </li>;
      {displayQuestion ? <ul>{questionsList}</ul> : null}
    </>  
  )
}

export default function DisplayQuizzes({quizzesList}){
    
    const listofQuizzes = quizzesList.map(quiz => {console.log('quiz',quiz); return <DisplaySingleQuiz quiz={quiz} />});
