import { useState } from 'react';

export default function DisplayQuizToFill(quiz){
  console.log("quiz to fill", quiz);
  console.log("quiz.qa", quiz.qa)
  const [index, setIndex] = useState(0);
  console.log("index", index);
  const next = () => setIndex((index + 1) % quiz.qa.length);
  let quizQuestion = quiz[index].question;
  let quizAnswer = quiz[index].answer;

  return(
    <>
    <h3>{quizQuestion}</h3>
    <h3>{quizAnswer}</h3>
    <button onClick={next}>Next Question</button>
    </>
  )

}



