import { useState } from 'react';

function QuizTitle({onAddTitle}){
  const [title, setTitle] = useState('');
  return (
    <>
      <input id="AddQuizTitle" type='text' placeholder='Add quiz title' value={title}  onChange={e => setTitle(e.target.value)} />
      <button id="btnAddQuizTitle" onClick={() => {setTitle(''); onAddTitle(title);}} >Add</button>
      {/* add ways to cominucate for element  with parent; Parent gives props and compoment uses what is is props to communicate back with parent */}
    </>
  )
}

function QuizBody(){
  return (
    <>
      <input id="EnterQuestion"type='text' placeholder='Enter a question'/>
      <input id="EnterAnswer" type='text' placeholder='Enter an answer'/>
      <button id="btnQAEnter">Add</button>
    </>
  )
}

