import { useState } from 'react';

export default function DisplayQuizToFill({quizz}){
  console.log("quiz to fill", quizz);
  console.log('quiz title', quizz.title);
  const [index, setIndex] = useState(0);
  const [disabled, setDisabled] = useState(false)
  console.log("index", index);
  let next;
  if (index >= quizz.qa.length) {
    next = () => setIndex((index + 1));
  } else {
    setDisabled(true);
  }
  let quizQuestion = quizz.qa[index].question;
  let quizAnswer = quizz.qa[index].answer;

  return(
    <>
    <h3>{quizQuestion}</h3>
    <h3>{quizAnswer}</h3>
    <button onClick={next} disabled={disabled} >Next Question</button>
    </>
  )

}



