function QuizName({quizTitle, setQuizTitle}){
  return (
    <>
      <input id="" type='text' placeholder='Add quiz title' value={quizTitle}  onChange={e => setQuizTitle(e.target.value)} />
    </>
  )
}

function QuizContent({quizQuestion, quizAnswer, editQuizQuestion, editQuizAnswer}){
  return (
    <>
      <input id="EnterQuestion"type='text' placeholder='Enter a question' value={quizQuestion} onChange={e => editQuizQuestion(e.target.value)}/>
      <input id="EnterAnswer" type='text' placeholder='Enter an answer' value={quizAnswer} onChange={e => editQuizAnswer(e.target.value)}/>

    </>
  )
}

function DislayQuizContent({questionList, handleEditQuizQestion, handleEditQuizAnswer}){
  const listItems = questionList.map((item, index) =>{ return <li key={item.id}> <QuizContent quizQuestion={item.question} quizAnswer={item.answer} editQuizQuestion={quizQuestion => handleEditQuizQestion(quizQuestion, index)} editQuizAnswer={quizAnswer => handleEditQuizAnswer(quizAnswer, index)}/> </li>})
  return (
    <div>
      <ul>{listItems}</ul>
    </div>
  )
}

function NextQuestionBtn ({onCreateNewQuestion}){
  return (
    <button id='nextQuestion' onClick={onCreateNewQuestion}>New Question</button>
  )
}

function SaveQuizBtn ({oncreateQuiz}){
  return (
    <button id="saveQuiz" onClick={oncreateQuiz}>Save Quiz</button> 
  )
}

export default function CreateQuiz({onSaveQuiz}){
  const [quiz, setQuiz] = useState({title:"", qa:[], lastQuestionId: 0});


  function handleAddQuizTitle(quizName) {
    const newQuiz = {...quiz};
    // console.log('skopiowany Quiz and title', newQuiz)
    newQuiz.title = quizName;
    // console.log("newQuiztitle", newQuiz.title);
    // console.log('newQuiz and title', newQuiz)
    setQuiz(newQuiz);
  }


  function handleEditQuizQestion(quizQuestion, questionIndex){
    const newQuiz = {...quiz};
    // console.log('skopiowany Quiz and question', newQuiz)
    newQuiz.qa = [...quiz.qa];
    // console.log("newQuiz.qa po skopiowaniu")
    newQuiz.qa[questionIndex].question = quizQuestion;
    // console.log("newQuiz and question", newQuiz);
    setQuiz(newQuiz);
  }

  function handleEditQuizAnswer(quizAnswer, questionIndex){
    const newQuiz = {...quiz};
    // console.log('skopiowany Quiz and question', newQuiz)
    newQuiz.qa = [...quiz.qa];
    // console.log("newQuiz.qa po skopiowaniu")
    newQuiz.qa[questionIndex].answer = quizAnswer;
    // console.log("newQuiz and question", newQuiz);
    setQuiz(newQuiz);
  }

  function handleCreateNewQuestion(){
    const newQuiz = {...quiz};
    newQuiz.qa = [...quiz.qa];
    let nextId = newQuiz.lastQuestionId + 1;
    newQuiz.lastQuestionId = nextId;
    newQuiz.qa.push(
      {question: '',
      answer :'',
      id: nextId}
    )
    setQuiz(newQuiz);
  }

  return (
    <div id='createQuizComponent'>
      <QuizName quizTitle={quiz.title} setQuizTitle={handleAddQuizTitle}/>
      <NextQuestionBtn onCreateNewQuestion={handleCreateNewQuestion}/>
      <SaveQuizBtn oncreateQuiz={() => onSaveQuiz(quiz)} />
      <DislayQuizContent questionList={quiz.qa} handleEditQuizQestion={handleEditQuizQestion} handleEditQuizAnswer={handleEditQuizAnswer}/>
    </div>  
  )
}








