import { useState } from 'react';
import DisplayEditQuiz  from './editQuiz.js';
import DisplayQuizToFill from './QuizMode.js';
// CreateQuiz({onSaveQuiz}){
//   const [quiz, setQuiz] = useState({title:"", qa:[{question:'', answer:'', id: 0}], lastQuestionId: 0});

// function DisplayEditQuiz({quiz}){

// }

function DisplaySingleQuiz({quiz, handleOnDeleteQuiz, onEditQuiz, toggleQuiz}){
  
  const [editQuiz, setEditQuiz] = useState(false);
  let displayContent;
  displayContent = (
    <>
      <button className='deleteQuizBtn' onClick={()=> handleOnDeleteQuiz(quiz.id)}>Delete Quiz</button>
        <div id="displayQuizTitle" onClick={()=>{toggleQuiz(quiz)}}>{quiz.title}</div>
        <button className='editQuizBtn' onClick={()=> {setEditQuiz(!editQuiz) }}>Edit Quiz</button>
    </>
  )

  function onEditQuiz2(quiz)  {
    onEditQuiz(quiz)
    setEditQuiz(!editQuiz)
    
  }
 
  return (
    <>
      <li className='quiz' > 
        {/* <button className='deleteQuizBtn' onClick={()=> handleOnDeleteQuiz(quiz.id)}>Delete Quiz</button>
        <div id="displayQuizTitle" onClick={()=>setdisplayQuestion(!displayQuestion)}>{quiz.title}</div>
        <button className='editQuizBtn' onClick={()=> {setEditQuiz(!editQuiz)}}>Edit Quiz</button> */}
        {editQuiz ? <DisplayEditQuiz oneQuiz={quiz} onSaveQuiz={onEditQuiz2} /> :  displayContent} 
      </li>
     
    </>  
  )
}

export default function DisplayQuizzes({quizzesList, handleOnDeleteQuiz, onEditQuiz}){
  const [displayQuestion, setdisplayQuestion] = useState(false);
  const [clikedQuiz, setClickedQuiz] = useState({})

  function toggleQuiz(quiz){
    setdisplayQuestion(!displayQuestion);
    setClickedQuiz(quiz)
  }

  // function hideQuestion(){
  //   setdisplayQuestion(false)
  // } 

  const listOfQuizzes = quizzesList.map(quiz => { return  <DisplaySingleQuiz  quiz={quiz}  toggleQuiz={toggleQuiz} handleOnDeleteQuiz={handleOnDeleteQuiz} onEditQuiz={onEditQuiz} key={quiz.id}/>} )
  return (
      <div>
        {!displayQuestion ? <ul>{listOfQuizzes}</ul> : <DisplayQuizToFill quizz={clikedQuiz}/> }
      </div>
        
      
    )
  }

