export function CreateQuiz(){
  const [quiz, setQuiz] = useState({title:"", qa:[], id: 0});
  console.log('quiz', quiz)

  function handleAddQuizTitle(quizName) {
    const newQuiz = {...quiz};
    console.log('skopiowany Quiz and title', newQuiz)
    newQuiz.title = quizName;
    // console.log("newQuiztitle", newQuiz.title);
    console.log('newQuiz and title', newQuiz)
    setQuiz(newQuiz);
  }

  // function handleAddQuizQA({quizQuestion, quizAnswer}){
  //   const newQuiz = {...quiz};
  //   console.log('skopiowany Quiz and question', newQuiz)
  //   newQuiz.qa = [...quiz.qa];
  //   console.log("newQuiz.qa po skopiowaniu")
  //   newQuiz.qa.push(
  //     {question: quizQuestion,
  //     answer : quizAnswer,
  //     id: nextId}
  //   )
  //   setNextId(nextId + 1);
  //   console.log("newQuiz and question", newQuiz);
  //   setQuiz(newQuiz);

  // }

  function handleAddQuizQestion({quizQuestion}){
    const newQuiz = {...quiz};
    let nextId = newQuiz.id + 1;
    console.log('skopiowany Quiz and question', newQuiz)
    newQuiz.qa = [...quiz.qa];
    console.log("newQuiz.qa po skopiowaniu")
    newQuiz.qa.push(
      {question: quizQuestion,
      id: nextId}
    )
    console.log("newQuiz and question", newQuiz);
    setQuiz(newQuiz);

  }


  return (
    <>
      <QuizName quizTitle={quiz.title} setQuizTitle={handleAddQuizTitle}/>
      <QuizContent quizQuestion={quiz.qa.question}  setQuizQuestion={handleAddQuizQestion} />
    </>
)
  

}








