import { useState } from 'react';
import DisplayEditQuiz  from './editQuiz.js';

// CreateQuiz({onSaveQuiz}){
//   const [quiz, setQuiz] = useState({title:"", qa:[{question:'', answer:'', id: 0}], lastQuestionId: 0});

// function DisplayEditQuiz({quiz}){

// }

function DisplaySingleQuiz({quiz, handleOnDeleteQuiz}){
  const [displayQuestion, setdisplayQuestion] = useState(false);
  const [editQuiz, setEditQuiz] = useState(false);
  
  console.log('quiz',quiz);
  // console.log('quiz id', quiz.id);
  // console.log('list of questions in quiz', quiz.qa);
  let questionsList = quiz.qa.map(questionItem => { return <li id='quizRecord' key={questionItem.id}>
    <div id='displayQuizQuestion'>{questionItem.question}</div> <div id='displayQuizAnswer'>{questionItem.answer}</div></li>})
  
 
  return (
    <>
      <li className='quiz' > 
        <button className='deleteQuizBtn' onClick={()=> handleOnDeleteQuiz(quiz.id)}>Delete Quiz</button>
        <div id="displayQuizTitle" onClick={()=>setdisplayQuestion(!displayQuestion)}>{quiz.title}</div>
        <button className='editQuizBtn' onClick={()=> setEditQuiz(!editQuiz)}>Edit Quiz</button>
          {displayQuestion ? <ul>{questionsList}</ul> : null} 
          {editQuiz ? <DisplayEditQuiz oneQuiz={quiz} /> : null}
          
      </li>
     
    </>  
  )
}

export default function DisplayQuizzes({quizzesList, handleOnDeleteQuiz}){

    const listOfQuizzes = quizzesList.map(quiz => { return  <DisplaySingleQuiz quiz={quiz} handleOnDeleteQuiz={handleOnDeleteQuiz} key={quiz.id}/>} )
    return (
      <div>
        <ul>{listOfQuizzes}</ul>
      </div>
        
      
    )
  }

