import QuizSetup from './QuizSetupPage.js';
import './App.css';
import { useState } from 'react';




function NavBar({onClickedHome}){
  return (
    <nav>
      <ul>
        <li onClick={onClickedHome} >Home</li>
        <li>My Quizzes</li>
        <li>Settings</li>
      </ul>
    </nav>
  )
}
function Header({goBackToHomePage}){
  return (
    <header>
      <h1>Quizzitch</h1>
      <NavBar onClickedHome={goBackToHomePage}/>
    </header>
  )
}

function Button({btnName, btnId, onClickedButton}){
  return (
    <button onClick={onClickedButton} id={btnId}>
      {btnName}
    </button>
  )
}


function SearchBar ({searchBarId}){
  return (
      <input id={searchBarId} type="text" placeholder="Search..."/>
  )
}

function Footer(){
  return(
    <p>&copy; shauvia</p>
  )
}

function QuizSetupPage(){
  return ( 
  <div className='quizSetupPage'>
    <QuizSetup/>
  </div>)  

}

