    <>
    {/* zrobić terery jesli kliknięte home, to wyswietla home page*/}

      {/* <Header goBackToHomePage={()=>setHomePageCliked(true)}/>  */}
      {homePageCliked ? (<Header goBackToHomePage={()=>setHomePageCliked(false)}/> && <HomePage onCreateNewQuiz={()=>setNewQuizCreated(false)}/>) : <Header goBackToHomePage={()=>setHomePageCliked(true)}/>}
      { newQuizCreated ? <QuizSetupPage /> : <HomePage onCreateNewQuiz={()=>setNewQuizCreated(true)}/>}
      {/* <QuizSetupPage /> */}

      <Footer/>
  </>
  )
}

