import { useState } from 'react';

function DisplaySingleQuiz({quiz}){
  const [displayQuestion, setdisplayQuestion] = useState(false)
  console.log('quiz',quiz);
  console.log('quiz id', quiz.id);
  console.log('list of questions in quiz', quiz.qa);
  let questionsList = quiz.qa.map(questionItem => {console.log('one record', questionItem ); return <li key={questionItem.id}>{questionItem.question}, {questionItem.answer}</li>})
 
  return (
    <>
      <li key={quiz.id} className='quiz' onClick={()=>setdisplayQuestion(!displayQuestion)}> 
        <>{quiz.title}</>
          {displayQuestion ? <ul>{questionsList}</ul> : null} 
      </li>
     
    </>  
  )
}

export default function DisplayQuizzes({quizzesList}){
    
    const listofQuizzes = quizzesList.map(quiz => { return <DisplaySingleQuiz quiz={quiz} key={quiz.id}/>})
  //   <ul>
  //   {questionList.map(qa => (
  //     <li key={qa.id}>{qa.question}</li>
  //   ))}
  // </ul>
  // </>
    return (
      <div className="quizQa">
        <ul>{listofQuizzes}</ul>
        
      </div>
        
      
    )
  }


